    function()
    {
        window.addEventListener(
            'load',
            function()
            {
                // Loop page links
                for(var i = 0, _link = document.querySelectorAll('.frame'); i < _link.length; i ++)
                {
                    // Skip empty links and Twitter
                    if(_link[i].href === '' || _link[i].href === '#' || _link[i].href.indexOf('twitter.com') > -1) continue;

                    _link[i].href = '/external/?external_link=' + encodeURIComponent(_link[i].href);
                }
            }
        );
    }
)();

