function dt_attachLoadEvent(callBack) {
	if (window.addEventListener) {                    
        window.addEventListener('load', callBack);
    } else if (window.attachEvent) {                  
        window.attachEvent('onload', callBack);
    }
}

dt_attachLoadEvent(function() {
	var now = new Date();
	var diff = 0;

var serverTime = 1725155735979;
diff = serverTime - now.getTime();

startTimer('p_lt_ctl07_pageplaceholder_p_lt_ctl01_DateTime','dddd, mmmm dd, yyyy - h:MM tt',diff);
});

