var rotateEvery = 7; //seconds
var images = [
    "https://lincolnil.gov/templates/design_control/images/1.jpg",
    "https://lincolnil.gov/templates/design_control/images/2.jpg",
    "https://lincolnil.gov/templates/design_control/images/3.jpg"];
var curImage = 0;
setInterval(delayFunction, rotateEvery*1000);
function delayFunction() {
  if(curImage == images.length){
    curImage = 0;
  }
  jQuery('#s5_header_area1').fadeOut(1000,function(){
    jQuery(this).css("background-image",'url('+images[curImage]+')').fadeIn(1000);
      curImage++;
  });

}

