function initMultibox(mbClass){
window.s5mbox = new multiBox({
mbClass: mbClass,/*class you need to add links that you want to trigger multiBox with (remember and update CSS files)*/
container: jQuery(document.body),/*where to inject multiBox*/
path: 'https://lincolnil.gov/templates/design_control/js/multibox/',/*path to mp3player and flvplayer etc*/
useOverlay: true,/*detect overlay setting*/
maxSize: {w:600, h:400},/*max dimensions (width,height) - set to null to disable resizing*/
movieSize: {w:400, h:300},
addDownload: false,/*do you want the files to be downloadable?*/
descClassName: 's5_multibox',/*the class name of the description divs*/
pathToDownloadScript: 'https://lincolnil.gov/templates/design_control/js/multibox/forceDownload.asp',/*if above is true, specify path to download script (classicASP and ASP.NET versions included)*/
addRollover: true,/*add rollover fade to each multibox link*/
addOverlayIcon: false,/*adds overlay icons to images within multibox links*/
addChain: false,/*cycle through all images fading them out then in*/
recalcTop: true,/*subtract the height of controls panel from top position*/
addTips: true,/*adds MooTools built in 'Tips' class to each element (see: http://mootools.net/docs/Plugins/Tips)*/
autoOpen: 0/*to auto open a multiBox element on page load change to (1, 2, or 3 etc)*/
});	}

