meta.script:
  Weglot.initialize({
      api_key: 'wg_7eefb1481d678f11315756e6f49e40f56'
  });

  // Listen for messages from the search component
  window.addEventListener("message", function(event){
      if (event.origin !== window.location.origin) {
          return;
      } else {
           if (event.data.type === 'translate' && event.data.message) {
               handleMessageFromSearchCmp(event.data);
           }
      }
  }, false);

  /**
   * This function translates user input into English
   * and returns the translation in a message.
   * 
   * @param {*} data - data sent from the search 
   * 					 component.
   * 
	 */
  function handleMessageFromSearchCmp(data) {
      if (data.type === 'translate' && data.message) {
          if (Weglot.getCurrentLang() !== 'en') {

              // Split search phrase into individual terms
              var termList = data.message.split(' ');
              var translatedTermList = [];
              for (const termIndex in termList) {
                  Weglot.search(termList[termIndex], (data) => {
                      translatedTermList.push(data);
                      if (translatedTermList.length === termList.length) {
                         	sendMessageToSearchCmp({'searchPhrase': translatedTermList.join(' '), 'type': 'translation'}); 
                      }
                  });
              }

         	}
          sendMessageToSearchCmp({'searchPhrase': data.message, 'type': 'translation'});
      }
  }

  /**
   * This function sends data out as a message
   * to the current window.
   *
   * @param {*} data - data to be sent to the 
   * 					 window
   * 
   */
  function sendMessageToSearchCmp(data) {
      window.postMessage(data, window.location.origin);
  }
