}
function OpenConfirm() {
    var LightBox = $find("ctl00_CP1_ConfirmLightBox");
    document.getElementById("formThankyou").innerHTML = "Thank You!";
    LightBox.show();
    //5.14.3.3
    setTimeout(function () {
        $("button[title='Close'").focus();
    }, 800);
}
function OpenConfirmMessage(message) {
