function checkRecap_772() {
    var hdnRecapVal = document.getElementById('CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1_ctl01_1_CR1_1_ctl00_1_hdnRecap_1');
    hdnRecapVal.value = '';
    for(var i = 0; i < captchas.length; i++) {
        if (captchas[i] == 'CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1_ctl01_1_CR1_1_ctl00_1_recaptcha_element_1') {
                hdnRecapVal.value = grecaptcha.getResponse(i);
        }
    }
    return hdnRecapVal.value;
}

