function SubmitForm_CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1(i) {
    var capData = '';
    if (grecaptcha) {
        capData = checkRecap_772();
    }
    if (Page_ClientValidate("group772" + i)) {
        var formdata = [];
         formdata.push(new nseFormData('CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1_ctl01_1_CR1_1_ctl00_0_txtText_0', 'CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1_ctl01_1_CR1_1_ctl00_0_txtText2_0', 773, 3)); 
        if (formdata.length > 0) {
            for (var i = 0; i < formdata.length; i++) {
                var f = formdata[i];
                if (f.Type == 1 || f.Type == 2 || f.Type == 3 || f.Type == 4) {
                    f.Value = document.getElementById(f.ClientID).value;
                }
                else if (f.Type == 5 || f.Type == 6) {
                    var v = "";
                    var o = document.getElementById(f.ClientID);
                    var oo = o.getElementsByTagName("input");;
                    for (var j = 0; j < oo.length; j++) {
                        if (oo[j].checked)
                            v = v + oo[j].value + ",";
                    }
                    f.Value = v;
                }
            }
        }
        nse7._0.Service.SubmitForm(56, 772, 0, formdata, capData, FormSuccess_CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1)
    }
}
function FormSuccess_CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1(result) {
    if (result) {
        ResetForm_CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1(); OpenConfirm();
    }
    else if (grecaptcha) {
        OpenInvalidMessage("Invalid request.");
    }

    if (grecaptcha)
        grecaptcha.reset();
}
function ResetForm_CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1() {
    var tabStrip = $find("ctl00_CP9_CR9_ctl01_ctl00_CR1_ctl00_ctl00_ContentRepeater1_ctl01_ctl00_RadTabStrip1");
    var tabs = tabStrip.get_tabs();
    tabs.getTab(0).select();
    var formdata = [];
     formdata.push(new nseFormData('CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1_ctl01_1_CR1_1_ctl00_0_txtText_0', 'CP9_CR9_ctl00_1_CR1_1_ctl00_0_ContentRepeater1_0_ctl00_1_ctl01_1_CR1_1_ctl00_0_txtText2_0', 773, 3)); 
    if (formdata.length > 0) {
        for (var i = 0; i < formdata.length; i++) {
            var f = formdata[i];
            if (f.Type == 1 || f.Type == 2 || f.Type == 4) {
                document.getElementById(f.ClientID).value = "";
            }
            else if (f.Type == 3) {
                document.getElementById(f.ClientID).value = "";
                var c2 = document.getElementById(f.ClientID2);
                if (c2)
                    c2.value = "";
            }
            else if (f.Type == 5 || f.Type == 6) {
                var o = document.getElementById(f.ClientID);
                var oo = o.getElementsByTagName("input");;
                for (var j = 0; j < oo.length; j++) {
                    oo[j].checked = false;
                }
            }
        }
    }
    document.getElementById("divConfirmMessage").innerHTML = document.getElementById("divConfirmOriginal").innerHTML;
}

        (function() {
            var calUrl = "/page/calrss/1068/52/calendar.rss?CT=3&CD=5&PD=1539&NW=0&AG=1";
            var $calendar = $("#CP4_CR4_ctl00_2_ezrssfeed_2");

            function InitControls() {
                console.log($calendar);
                var $feed = $calendar.find(".ezrssfeedcontent");
                var $horizontalFeed = $feed.find(".ezrss-horizontal");
                var $rssItems = $calendar.find(".ezitem");
                $rssItems.each(function() {
                    //Gets the RSS URL and links it to the "Read more" span tag.
                    var linkEl = $(this).find(".eztitlelink");
                    var href = linkEl.attr("href");
                    var target = linkEl.attr("target");
                    var readmore = $(this).find(".ezmorelink");
                    readmore.on("click", function() {
                        if (target) window.open(href);
                        else window.location = href;
                    });
                });
                if ($horizontalFeed.length) {
                    var $events = $horizontalFeed.find(".ezrss-item");
                    var tallest = 0;
                    if ($events.length) {
                        for (var j = 0; j < $events.length; j++) {
                            if ($events[j].clientHeight > tallest) tallest = $events[j].clientHeight; //Find the event with the tallest height
                        }
                        for (var k = 0; k < $events.length; k++) {
                            $events[k].style.height = tallest + "px"; //Apply the tallest height to all of the events
                        }
                    }
                }
            }

            function GetCalendar() {
                var $isNotObject = $calendar.find(".ezrssfeedin");
                if ($isNotObject.length) {
                    $isNotObject.addClass("ezrssfeedin--loading");
                } else {
                    var $child = $calendar.children().eq(0);
                    $child.wrap("
").wrap("
");
                    $calendar.find(".ezrssfeedin--loading").prepend('




  ');
                }
                $.ajax({
                    type: 'GET',
                    url: calUrl,
                    dataType: "html",
                    async: true,
                    cache: false,
                    success: function(data) {
                        if ($isNotObject.length) {
                            $isNotObject.addClass("ezrssfeedin--done").removeClass("ezrssfeedin--loading");
                            $calendar.find(".ezrssfeedcontent").html(data);
                            InitControls();
                        } else {
                            $("#CP4_CR4_ctl00_2_ezrssfeed_2").html(data).find(".ezrssfeedin").addClass("ezrssfeedin--done").removeClass("ezrssfeedin--loading");
                        }
                    }
                });
            }

            if (calUrl)
                setTimeout(GetCalendar, 200);
            else
                InitControls();

        })();

