$(document).ready(function() {
	setTimeout(function() {
		setHeight($("#accolades .thumbnail"));
		setHeight($("#heads-up .well"));
	}, 500);

	$(window).on('resize', function() {
		$("#heads-up .well, #accolades .thumbnail").css("height", "auto");
		setHeight($("#accolades .thumbnail"));
		setHeight($("#heads-up .well"));
	});

	function setHeight(obj) {
		var longest = 0;
		obj.each(function(i, item) {
			if ($(item).outerHeight() > longest)
				longest = $(item).outerHeight();
		}).height(longest);
	}
});

