(function() {
  var feed = new EZTemplate("https://www.staytonoregon.gov//json/communicator/3733/8", "#NewsTemplate6", "#NewsContainer6");
  // This code below is used to enable the scrolling rss feed
  feed.afterRender(function($element) {
    $element.ezScroller({
      direction: "horizontal",
      speed: 500,
      timeout: 15000,
      auto: true,
      nextButton: $("#next"),
      prevButton: $("#prev")
    });
  });
})();

