		$(document).ready(function () {
			try {
				$(".widgetSearchButton.widgetSearchButtonf97e9776-4558-4e0e-906c-ae75b89236dd").click(function (e) {
				e.preventDefault();
					if ($("#ysnSearchOnlyDeptf97e9776-4558-4e0e-906c-ae75b89236dd").is(':checked')) {
						doWidgetSearch($(this).siblings(".widgetSearchBox").val(), Number(0));
					} else {
						doWidgetSearch($(this).siblings(".widgetSearchBox").val(), 0);
					}
			});
			$("#searchFieldf97e9776-4558-4e0e-906c-ae75b89236dd").keypress(function (e) {
				if (window.clipboardData) {
					if (e.keyCode == 13) {
							if ($("#ysnSearchOnlyDeptf97e9776-4558-4e0e-906c-ae75b89236dd").is(':checked')) {
								doWidgetSearch($(this).val(), Number(0));
							} else {
								doWidgetSearch($(this).val(), 0);
							}
						return false;
					}
				} else {
					if (e.which == 13) {
							if ($("#ysnSearchOnlyDeptf97e9776-4558-4e0e-906c-ae75b89236dd").is(':checked')) {
								doWidgetSearch($(this).val(), Number(0));
							} else {
								doWidgetSearch($(this).val(), 0);
							}
						return false;
					}
				}
				return true;
			});

				if (true) {
					var currentRequest = null;

					var $searchField = $("#searchFieldf97e9776-4558-4e0e-906c-ae75b89236dd").autocomplete({
						source: function (request, response) {
							currentRequest = $.ajax({
								url: '/Search/AutoComplete',
								dataType: "json",
								timeout: 10000,
								beforeSend: function () {
									if (currentRequest != null) {
										currentRequest.abort();
									}
								},
								data:
								{
									term: request.term,
								},
								success: function (data) {
									response(data);
									$('.autoCompleteError').remove();
								},
								error: function (xmlhttprequest, textstatus, message) {
									if (textstatus === "timeout") {
										if ($("#searchFieldf97e9776-4558-4e0e-906c-ae75b89236dd").siblings('.autoCompleteError').length == 0)
											$('
Search autocomplete is currently not responding. Please try again later.').insertAfter($("#searchFieldf97e9776-4558-4e0e-906c-ae75b89236dd"));
									}
								}
							});
						},
						html: true,
						delay: 500,
						select: function (event, ui) {
							$(this).val(ui.item.value);
							$(this).next().click();
						}
					});

					if (true && true) {
						$searchField.data("ui-autocomplete")._renderItem = function (ul, item) {
							return $("
")
								.data("ui-autocomplete-item", item)
								.append("" + item.label + "")
								.appendTo(ul);
						};
					}
					else {
						$searchField.data("autocomplete")._renderItem = function (ul, item) {
							return $("
")
								.data("item.autocomplete", item)
								.append("" + item.label + "")
								.appendTo(ul);
						};
					}
}
}
			catch(e) {} //we're going to eat this error. Autocomplete won't work but we dont wan't to break anything else on the page.
		});

