		}, 100);
	};
	cpMedia.register('screen and (max-width: 50em)', {
		setup: function () {
			window.setTimeout(setupExtraToolbar, 220);
		},
		match: function () {
			$fixedBar.css('margin-top', 'auto');
			$contentShift.css('padding-top', 'auto');
		},
		unmatch: function () {
			setupExtraToolbar();
		}
	});
	window.Pages.onResizeHandlers.push(setupExtraToolbar);
});
