	window.open("https://www.google.com/search?q=" + v + "&sitesearch=https://vctx.org", "google", "");

}
function do_google_keyup(e, t) {
  if (e.keyCode == 13) {
	  do_google_search(t.value);
	e.preventDefault();
  }
}

function do_google_key(e) {
  if (e.keyCode == 13) {
	e.preventDefault();
  }
}

